 body: any, @Response() res) { const loginUser = Object.assign(new AdminLoginDto(), body) const { password: correctPassword, slogon } = this.getAdminConfig(); const errors = await validate(loginUser, {forbidUnknownValues: false}); if (errors.length &gt; 0) { throw new BadRequestException(errors); } if (loginUser.username !== this.username || loginUser.password !== correctPassword) { throw new HttpException('Invalid credentials', HttpStatus.UNAUTHORIZED); } const payload = { username: this.username, slogon }; const token = this.jwtService.sign(payload); res.cookie('token', token, { httpOnly: true }); return res.status(200).json({ message: '登录成功' }); } @Get('login') @Render('login') renderLogin() { } @Get('index') // @Render(&quot;admin&quot;) renderAdmin(@Request() req, @Response() res) { console.log(req.cookies) const token = req.cookies.token; if (!token) { return res.status(401).json({ message: '未授权' }); } try { const decoded = this.jwtService.verify(token); const profile = gray.stringify(gray(decoded.slogon).content, {username: decoded.username}) console.log(profile) res.render('admin', {&quot;info&quot;: profile}); } catch (error) { return res.status(401).json({ message: '无效的令牌' }); } } @Get('changePassword') // @Render(&quot;admin&quot;) change(@Request() req, @Response() res) { console.log(req.cookies) const token = req.cookies.token; if (!token) { return res.status(401).json({ message: '未授权' }); } try { const decoded = this.jwtService.verify(token); res.render('change'); } catch (error) { return res.status(401).json({ message: '无效的令牌' }); } } @Post('changePassword') async changePassword(@Request() req, @Body() body: any, @Response() res) { const token = req.cookies.token; const { password, slogon } = this.getAdminConfig(); const newUser = Object.assign(new AdminLoginDto(), body) const errors = await validate(newUser, {forbidUnknownValues: false}); if (errors.length &gt; 0) { throw new BadRequestException(errors); } if (!token) { return res.status(401).json({ 